let canvas = document.getElementById('confetti');

 canvas.width = 600;
 canvas.height = 580;

let ctx = canvas.getContext('2d');
let pieces = [];
let numberOfPieces = 200;
let lastUpdateTime = Date.now();

function randomColor() {
    let colors = ['#ff0000', '#86FF70', '#0000ff', '#00ffff', '#ff00ff', '#ffff00', '#ff8f00','#ff0080','#bffff0'];
    return colors[Math.floor(Math.random() * colors.length)];
}

function update() {
    let now = Date.now(),
        dt = now - lastUpdateTime;

    for (let i = pieces.length - 1; i >= 0; i--) {
        let p = pieces[i];

        if (p.y > canvas.height) {
            pieces.splice(i, 1);
            continue;
        }
        p.y += p.gravity * dt;
        p.rotation += p.rotationSpeed * dt;
    }

    while (pieces.length < numberOfPieces) {
        pieces.push(new Piece(Math.random() * canvas.width, -10));
    }

    lastUpdateTime = now;

    setTimeout(update, 1);
}

function draw() {
    ctx.clearRect(0, 0, canvas.width, canvas.height);

    pieces.forEach(function (p) {
        ctx.save();

        ctx.fillStyle = p.color;

        ctx.translate(p.x + p.size / 1000, p.y + p.size / 1000);
        ctx.rotate(p.rotation);

        ctx.fillRect(-p.size / 1000, -p.x / 1000, p.size, p.size);

        ctx.restore();
    });
    requestAnimationFrame(draw);
}

function Piece(x, y) {
    this.x = x;
    this.y = y;
    this.size = (Math.random() * .01 + .75) * 5;
    this.gravity = (Math.random() * .1 + .95) * .04;
    this.rotation = (Math.PI * 2) * Math.random();
    this.rotationSpeed = (Math.PI * 2) * Math.random() * .0005;
    this.color = randomColor();
}

while (pieces.length < numberOfPieces) {
    pieces.push(new Piece(Math.random() * canvas.width, Math.random() * canvas.height));
}

update();
draw();
